16 AdjointOptimization::AdjointOptimization(
17     Waveguide* in_waveguide_primal, MeshGenerator* in_mg,
18     SpaceTransformation* in_st_primal, SpaceTransformation* in_st_dual,
19     OptimizationAlgorithm<std::complex<double>>* in_Oa) {
20   waveguide = in_waveguide_primal;
21   primal_st = in_st_primal;
22   dual_st = in_st_dual;
23   mg = in_mg;
24   oa = in_Oa;
