29 std::vector<std::complex<double>> AdjointOptimization::compute_small_step(
30     double step) {
31   waveguide->switch_to_primal(primal_st);
32   std::complex<double> global_a_out = primal_st->evaluate_for_z(
33       GlobalParams.M_R_ZLength / 2.0 - 0.0001, waveguide);
34   std::vector<std::complex<double>> grad =
35       waveguide->assemble_adjoint_local_contribution(step);
36   for (unsigned int i = 0; i < grad.size(); i++) {
37     deallog << "Phase Derivative: " << grad[i] / step << " Step: " << step
38             << "Quality derivative: "
39             << std::abs(global_a_out + grad[i]) - std::abs(global_a_out)
40             << std::endl;
41   }
42   return grad;
43 }
