45 double AdjointOptimization::compute_big_step(std::vector<double> step) {
46   Vector<double> current_config = primal_st->Dofs();
47 
48   for (unsigned int i = 0; i < step.size(); i++) {
49     primal_st->set_dof(i, current_config[i] + step[i]);
50     dual_st->set_dof(i, current_config[i] + step[i]);
51   }
52 
53   deallog.push("Config");
54   Vector<double> for_output = primal_st->Dofs();
55   for (unsigned int i = 0; i < step.size(); i++) {
56     deallog << for_output[i] << " , ";
57   }
58   deallog << std::endl;
59   deallog.pop();
60 
61   MPI_Barrier(MPI_COMM_WORLD);
62   waveguide->switch_to_primal(primal_st);
63   waveguide->run();
64   MPI_Barrier(MPI_COMM_WORLD);
65   double quality = 0;
66   std::complex<double> a_in = primal_st->evaluate_for_z_with_sum(
67       -GlobalParams.M_R_ZLength / 2.0, 2.0 * GlobalParams.M_C_Dim1In,
68       waveguide);
69   std::complex<double> a_out = primal_st->evaluate_for_z_with_sum(
70       GlobalParams.M_R_ZLength / 2.0, 2.0 * GlobalParams.M_C_Dim1In, waveguide);
71   deallog << "Phase in (@ " << -GlobalParams.M_R_ZLength / 2.0 << ": " << a_in
72           << std::endl;
73   deallog << "Phase out(@ " << GlobalParams.M_R_ZLength / 2.0 << ": " << a_out
74           << std::endl;
75   quality = std::abs(a_out / a_in);
76   deallog << "Computed primal quality " << quality << std::endl;
77   // New starts here
78   const double step_width = 0.01;
79   unsigned int cnt_steps = 0;
80   double z_temp = GlobalParams.Minimum_Z;
81   while (z_temp < GlobalParams.Maximum_Z) {
82     cnt_steps++;
83     z_temp += step_width;
84   }
85   deallog << "Start" << std::endl;
86   bool* mine = new bool[cnt_steps];
87   z_temp = GlobalParams.Minimum_Z + 0.0001;
88   unsigned int own_cnt = 0;
89   double lowest_own = GlobalParams.Minimum_Z - 10.0;
90   int lowest_idx = 0;
91   for (unsigned int i = 0; i < cnt_steps; i++) {
92     if (z_temp > this->waveguide->mg->z_min &&
93         z_temp < this->waveguide->mg->z_max) {
94       mine[i] = true;
95       if (own_cnt == 0) {
96         lowest_own = z_temp;
97         lowest_idx = i;
98       }
99       own_cnt++;
100     } else {
101       mine[i] = false;
102     }
103     z_temp += step_width;
104   }
105   int* displs;
106   if (GlobalParams.MPI_Rank == 0) {
107     displs = new int[GlobalParams.NumberProcesses];
108   }
109   std::cout << "I am " << GlobalParams.MPI_Rank << ". I have lowest own "
110             << lowest_own << " and own_cnt " << own_cnt << std::endl;
111   MPI_Gather(&lowest_idx, 1, MPI_INTEGER, displs, 1, MPI_INTEGER, 0,
112              MPI_COMM_WORLD);
113   double* a_reals = new double[own_cnt];
114   double* a_imags = new double[own_cnt];
115   double* a_abolutes = new double[own_cnt];
116   z_temp = lowest_own;
117   for (unsigned int i = 0; i < own_cnt - 1; i++) {
118     std::complex<double> l_val(0, 0);
119     try {
120       deallog << "Executing for " << z_temp << std::endl;
121       l_val = primal_st->evaluate_for_z(z_temp, waveguide);
122     } catch (...) {
123       std::cout << "In Process " << GlobalParams.MPI_Rank << ": Broke for "
124                 << z_temp << std::endl;
125     }
126     a_reals[i] = l_val.real();
127     a_imags[i] = l_val.imag();
128     a_abolutes[i] =
129         std::sqrt(a_reals[i] * a_reals[i] + a_imags[i] * a_imags[i]);
130     z_temp += step_width;
131   }
132   std::cout << "I am " << GlobalParams.MPI_Rank << ". My dofs are ";
133   for (int i = 0; i < cnt_steps; i++) {
134     if (mine[i]) {
135       std::cout << i << " ";
136     }
137   }
138   std::cout << std::endl;
139 
140   MPI_Barrier(MPI_COMM_WORLD);
141   double* all_reals;
142   double* all_imags;
143   double* all_absolutes;
144   int* cnt_recv;
145   if (GlobalParams.MPI_Rank == 0) {
146     all_reals = new double[cnt_steps];
147     all_imags = new double[cnt_steps];
148     all_absolutes = new double[cnt_steps];
149     cnt_recv = new int[GlobalParams.NumberProcesses];
150     cnt_recv[0] = own_cnt;
151     for (unsigned int i = 1; i < GlobalParams.NumberProcesses - 1; i++) {
152       cnt_recv[i] = displs[i + 1] - displs[i];
153     }
154     cnt_recv[GlobalParams.NumberProcesses - 1] =
155         cnt_steps - displs[GlobalParams.NumberProcesses - 1];
156   }
157   deallog << "Output proc " << GlobalParams.MPI_Rank << "." << std::endl;
158   for (unsigned int i = 0; i < own_cnt; i++) {
159     deallog << a_reals[i] << " " << a_imags[i] << " " << a_abolutes[i]
160             << std::endl;
161   }
162   std::cout << "Ready proc " << GlobalParams.MPI_Rank << "." << std::endl;
163   MPI_Gatherv(a_reals, own_cnt, MPI_DOUBLE, all_reals, cnt_recv, displs,
164               MPI_DOUBLE, 0, MPI_COMM_WORLD);
165   MPI_Gatherv(a_imags, own_cnt, MPI_DOUBLE, all_imags, cnt_recv, displs,
166               MPI_DOUBLE, 0, MPI_COMM_WORLD);
167   MPI_Gatherv(a_abolutes, own_cnt, MPI_DOUBLE, all_absolutes, cnt_recv, displs,
168               MPI_DOUBLE, 0, MPI_COMM_WORLD);
169   if (GlobalParams.MPI_Rank == 0) {
170     std::ofstream result_file;
171     result_file.open((solutionpath + "/ComplexQualities.dat").c_str(),
172                      std::ios_base::openmode::_S_trunc);
173     result_file << "z \t re(f) \t im(f) \t |f|" << std::endl;
174     z_temp = GlobalParams.Minimum_Z;
175     for (unsigned int i = 0; i < cnt_steps; i++) {
176       result_file << z_temp << "\t" << all_reals[i] << "\t" << all_imags[i]
177                   << "\t" << all_absolutes[i] << std::endl;
178       z_temp += step_width;
179     }
180     result_file.close();
181   }
182 
183   MPI_Barrier(MPI_COMM_WORLD);
184   waveguide->switch_to_dual(dual_st);
185   waveguide->run();
186   MPI_Barrier(MPI_COMM_WORLD);
187   double dual_quality = 0;
188   std::complex<double> d_a_in =
189       primal_st->evaluate_for_z(-GlobalParams.M_R_ZLength / 2.0, waveguide);
190   std::complex<double> d_a_out =
191       primal_st->evaluate_for_z(GlobalParams.M_R_ZLength / 2.0, waveguide);
192   dual_quality = std::abs(d_a_out / d_a_in);
193   deallog << "Phase in: " << d_a_in << std::endl;
194   deallog << "Phase out: " << d_a_out << std::endl;
195 
196   deallog << "Computed dual quality " << dual_quality << std::endl;
197   waveguide->switch_to_primal(primal_st);
198   return quality;
199 }
200 
201 void AdjointOptimization::run() {
202   Convergence_Table.set_auto_fill_mode(true);
203   bool run = true;
204   int counter = 0;
205   double quality = 0;
206 
207   while (run) {
208     int small_steps = 0;
209     while (oa->perform_small_step_next(small_steps)) {
210       deallog << "Performing a small step." << std::endl;
211       double temp_step_width = oa->get_small_step_step_width(small_steps);
212       oa->pass_result_small_step(compute_small_step(temp_step_width));
213       small_steps++;
214     }
215 
216     if (oa->perform_big_step_next(small_steps)) {
217       deallog << "Performing a big step." << std::endl;
218       std::vector<double> step = oa->get_big_step_configuration();
219       deallog << "Got the following big step configuration: ";
220       for (unsigned int i = 0; i < step.size(); i++) {
221         deallog << step[i] << " , ";
222       }
223       deallog << std::endl;
224       quality = compute_big_step(step);
225       oa->pass_result_big_step(
226           primal_st->evaluate_for_z(GlobalParams.M_R_ZLength / 2.0, waveguide));
227     }
228 
229     counter++;
230 
231     if (counter > GlobalParams.Sc_OptimizationSteps || quality > 1.0) {
232       deallog << "The optimization is shutting down after " << counter
233               << " steps. Last quality: " << 100 * quality << "%." << std::endl;
234       run = false;
235     }
236 
237     if ((GlobalParams.O_C_D_ConvergenceFirst ||
238          GlobalParams.O_C_D_ConvergenceAll) &&
239         (GlobalParams.MPI_Rank == 0)) {
240       std::ofstream result_file;
241       result_file.open((solutionpath + "/convergence_rates.dat").c_str(),
242                        std::ios_base::openmode::_S_trunc);
243 
244       Convergence_Table.write_text(
245           result_file,
246           dealii::TableHandler::TextOutputFormat::table_with_headers);
247       result_file.close();
248       result_file.open((solutionpath + "/convergence_rates.tex").c_str(),
249                        std::ios_base::openmode::_S_trunc);
250       Convergence_Table.write_tex(result_file);
251       result_file.close();
252 
253       result_file.open((solutionpath + "/steps.dat").c_str(),
254                        std::ios_base::openmode::_S_trunc);
255       oa->WriteStepsOut(result_file);
256       result_file.close();
257     }
258   }
259 }
