27 bool Optimization1D::perform_small_step_next(int small_steps_before ){
28   if(residuals.size() == 0 && states.size() == 0) {
29       return false;
30   }
31 
32   if (small_steps_before < STEPS_PER_DOFS) {
33     return true;
34   } else {
35     return false;
36   }
37 }
