39 double Optimization1D::get_small_step_step_width(int small_steps_before ){
40 
41   if(small_steps_before < STEPS_PER_DOFS && small_steps_before >= 0) {
42     return steps_widths[small_steps_before];
43   } else {
44     std::cout<< "Warning in Optimization1D::get_small_step_step_width(int)" <<std::endl;
45     return -1.0;
46   }
47 }
