49 bool Optimization1D::perform_big_step_next(int small_steps_before ) {
50     if(residuals.size() == 0 && states.size() == 0) {
51           return true;
52     }
53 
54     return (small_steps_before >=STEPS_PER_DOFS);
55 }
56 
57 std::vector<double> Optimization1D::get_big_step_configuration(){
58   std::vector<double> ret;
59   if(residuals.size() == 0 && states.size() == 0) {
60       return ret;
61   }
62   int small_step_count = states.size() ;
63   int big_step_count = residuals.size() ;
64 
65   if( big_step_count == 0 || (small_step_count != STEPS_PER_DOFS* big_step_count ) ) {
66     std::cout << "Warning in Optimization1D::get_big_step_configuration()" <<std::endl;
67   } else {
68     std::complex<double> residual = residuals[big_step_count-1];
69     double state_red = std::abs(residual);
70     unsigned int ndofs = states[small_step_count-1].size();
71     ret.resize(ndofs);
72     for ( unsigned int i = 0; i < ndofs; i++ ) {
73       double max = 0;
74       int index =-1;
75       for(unsigned int j = 0; j<STEPS_PER_DOFS; j++) {
76         double magn = std::abs(residual + states[small_step_count - STEPS_PER_DOFS + j][i]);
77         if(magn > max && magn > state_red){
78           max = magn;
79           index = j;
80         }
81       }
82       if(index != -1) {
83         ret[i] = steps_widths[index];
84       } else {
85         ret[i] = 0.0;
86       }
87 
88     }
89   }
90   return ret;
91 }
